use revm_interpreter::opcode::{INVALID, JUMP, JUMPI, RETURN, REVERT, STOP};

#[cfg(not(test))]
pub static mut SKIP_CBOR: bool = false;
#[cfg(test)]
pub static mut SKIP_CBOR: bool = true;

pub fn walk_bytecode<Fn: FnOnce(usize, u8) + Copy>(bytes: Vec<u8>, it: Fn) {
    let mut i = 0;
    let last_op = *bytes.last().unwrap();
    let has_cbor = last_op != JUMP ||
        last_op != JUMPI ||
        last_op != STOP ||
        last_op != INVALID ||
        last_op != REVERT ||
        last_op != RETURN;

    let cbor_len = if has_cbor && !unsafe { SKIP_CBOR } {
        // load last 2 bytes as big endian
        let len = bytes.len();
        let last_2 = *bytes.get(len - 2).unwrap() as usize;
        let last_1 = *bytes.get(len - 1).unwrap() as usize;
        (last_2 << 8) + last_1 + 2
    } else {
        0
    };

    while i < bytes.len() - cbor_len {
        let op = *bytes.get(i).unwrap();
        it(i, op);
        i += 1;
        if (0x60..=0x7f).contains(&op) {
            i += op as usize - 0x5f;
        }
    }
}

pub fn all_bytecode(bytes: &Vec<u8>) -> Vec<(usize, u8)> {
    if bytes.is_empty() {
        return vec![];
    }

    // remove ending 00
    let mut bytes = bytes.clone();
    while bytes.last().unwrap_or(&1) == &0 {
        bytes.pop();
    }

    let mut i = 0;
    let last_op = *bytes.last().unwrap_or(&RETURN);
    let has_cbor = last_op != JUMP &&
        last_op != JUMPI &&
        last_op != STOP &&
        last_op != INVALID &&
        last_op != REVERT &&
        last_op != RETURN;

    let cbor_len = if has_cbor && !unsafe { SKIP_CBOR } {
        // load last 2 bytes as big endian
        let len = bytes.len();
        let last_2 = *bytes.get(len - 2).unwrap() as usize;
        let last_1 = *bytes.get(len - 1).unwrap() as usize;
        (last_2 << 8) + last_1 + 2
    } else {
        0
    };
    // println!("cbor_len: {} lastop: {}", cbor_len, last_op);

    let mut res = Vec::new();

    while i < bytes.len() - cbor_len {
        let op = if let Some(op) = bytes.get(i) {
            *op
        } else {
            break;
        };
        res.push((i, op));
        i += 1;
        if (0x60..=0x7f).contains(&op) {
            i += op as usize - 0x5f;
        }
    }
    // println!("res: {:?}", res.len());
    res
}

#[macro_export]
macro_rules! skip_cbor {
    ($e: expr) => {{
        #[cfg(not(test))]
        unsafe {
            SKIP_CBOR = true;
        }
        let res = $e;
        #[cfg(not(test))]
        unsafe {
            SKIP_CBOR = false;
        }
        res
    }};
}

#[cfg(test)]
mod test {
    use tracing::debug;

    use crate::evm::bytecode_iterator::walk_bytecode;

    #[test]
    fn test_walk_bytecode() {
        let bytes = hex::decode("").expect("");
        let f = |v: usize, k: u8| {
            debug!("{} {}", v, k);
        };
        walk_bytecode(bytes, f)
    }
}
