    #[test]
    fn test_extract_function_hash() {
        use crate::evm::bytecode_iterator::SKIP_CBOR;
        // uniswap v2 router
        let code = "";
        let sigs = skip_cbor!({
            extract_sig_from_contract(code)
                .iter()
                .map(|x| hex::encode(x))
                .collect_vec()
        });
        println!("{}: {:?}", sigs.len(), sigs);

        // counted from etherscan lol
        assert_eq!(sigs.len(), 24);
    }

