pub static mut BASE_PATH: String = String::new();

#[derive(Debug,Clone)]
pub struct SourceMapLocation {
    pub file: Option<String>,
    pub offset: usize,
    pub length: usize,
}

impl SourceMapLocation {
    pub fn new(file: Option<String>, offset: usize, length: usize) -> Self {
        Self {
            file,
            offset,
            length,
        }
    }
}

impl Default for SourceMapLocation {
    fn default() -> Self {
        Self {
            file: None,
            offset: 0,
            length: 0,
        }
    }
}

pub enum SourceMapAvailability {
    Available(String),
    Unavailable,
    Unknown,
}

fn read_source_code(loc: &SourceMapLocation) -> String {
    let file = loc.file.clone().unwrap();
    let offset = loc.offset;
    let length = loc.length;

    let mut file = match File::open(unsafe {BASE_PATH.clone()} + file.as_str()) {
        Ok(f) => f,
        Err(e) => {
            return format!("{:?}:{:?}", offset, length);
        }
    };

    let mut contents = String::new();
    file.read_to_string(&mut contents).unwrap();

    // get starting and ending line number
    let mut line_number = 1;
    let mut start_line = 0;
    let mut end_line = 0;

    for (i, c) in contents.chars().enumerate() {
        if i == offset {
            start_line = line_number;
        }
        if i == offset + length {
            end_line = line_number;
            break;
        }
        if c == '\n' {
            line_number += 1;
        }
    }

    let lines_in_range = contents.lines()
        .skip(start_line)
        .take(end_line - start_line + 1)
        .join("\n");

    format!("\n{}", lines_in_range)
}

pub fn pretty_print_source_map(pc: usize, addr: &EVMAddress, data: &ProjectSourceMapTy) -> SourceMapAvailability {
    match data.get(addr) {
        Some(Some(contract_data)) => {
            match contract_data.get(&pc) {
                Some(info) => {
                    match info.file {
                        Some(ref file) => {
                            SourceMapAvailability::Available(format!("{}:{}", file, read_source_code(info)))
                        }
                        None => SourceMapAvailability::Unknown
                    }
                }
                None => SourceMapAvailability::Unknown
            }
        }
        _ => SourceMapAvailability::Unavailable
    }
}

pub fn uncompress_srcmap_single(map: String, files: &Vec<String>) -> Vec<SourceMapLocation> {
    let mut results: Vec<SourceMapLocation> = vec![];

    let mut counter = 0;
    for part in map.split(';') {
        let parts = part.split(':').collect::<Vec<&str>>();
        let parts_len = parts.len();

        let has_offset = parts_len > 0 && parts[0] != "";
        let has_length = parts_len > 1 && parts[1] != "";
        let has_file = parts_len > 2 && parts[2] != "";
        let has_jump = parts_len > 3 && parts[3] != "";

        let has_everything = has_offset && has_length && has_file && has_jump;

        if counter == 0 && !has_everything {
            results.push(
                SourceMapLocation::default()
            );
        } else {
            results.push(
                SourceMapLocation::new(
                    if has_file {
                        let idx = parts[2].parse::<usize>().unwrap_or(usize::MAX);
                        if idx < files.len() {
                            Some(files[idx].clone())
                        } else {
                            None
                        }
                    } else {
                        results[counter - 1].file.clone()
                    },
                    if has_offset && let Ok(res) = parts[0].parse::<usize>() {
                        res
                    } else {
                        results[counter - 1].offset
                    },
                    if has_length && let Ok(res) = parts[1].parse::<usize>() {
                        res
                    } else {
                        results[counter - 1].length
                    }
                )
            )
        }

        counter += 1;
    }

    results
}

pub fn decode_instructions(bytecode: Vec<u8>, map: String, files: &Vec<String>) -> HashMap<usize, SourceMapLocation> {
    let mut results: HashMap<usize, SourceMapLocation> = Default::default();
    let mut uncompressed_map = uncompress_srcmap_single(map, files);
    let bytecode_len = bytecode.len();

    let mut idx = 0;
    let mut srcmap_idx = 0;

    loop {
        if idx >= bytecode_len {
            break;
        }
        let opcode = bytecode[idx];
        let srcmap = uncompressed_map.get(srcmap_idx);
        match srcmap {
            Some(srcmap) => {
                results.insert(idx, srcmap.clone());
            }
            None => { }
        }


        match opcode {
            0x60..=0x7f => {
                idx += opcode as usize - 0x5e;
            }
            _ => {
                idx += 1;
            }
        }

        srcmap_idx += 1;
    }
    results
}


